import { Directive } from '@angular/core';

@Directive({
  selector: '[appMngvalue]'
})
export class MngvalueDirective {

 @Input() appMngvalue:string = "";

  constructor(private el:ElementRef) 
  {

  }

  ngOnInit()
  {
	this.el.nativeElement.value = this.appMngvalue;
  } 

}
