import { Directive,ElementRef, Input, HostListener } from '@angular/core';

@Directive({
  selector: '[appMngtype]'
})
export class MngtypeDirective {

  @Input() appMngtype:string = "";

  constructor(el:ElementRef) 
  {
	el.nativeElement.type = this.appMngtype;
  }

}
